<?phpnamespace Daniel\Curriculo;/** * Class Curriculo * @package Daniel\Curriculo * @property-read string $nome * @property-read string $email * @property-read string $linkedin * @property-read string $github * */class Mylife{    private  $eat;    private  $sleep;    private  $coder;    private  $repeat;    public function __construct(string $eat, string $sleep, string $coder, string $repeat)    {        $this->eat = $eat;        $this->sleep = $sleep;        $this->coder = $coder;        $this->repeat = $repeat;    }    public function getDay($life): string    {        while($life) {            $this->eat;            $this->sleep;            $this->coder;            $this->repeat;        }    }}$curriculo = new Curriculo('@', '@', '@', '@');echo $curriculo->getCurriculo();